import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { EMPTY, Observable, catchError, concat, from, interval, map, mergeMap, of, retry, switchMap, take, tap, throwError } from 'rxjs';
import { environment } from 'src/environments/environment.development';
import { FlightsService } from './flights.service';
import { IWorker } from '../components/models/worker.model';
import { IFlight } from '../components/models/flights.model';
@Injectable({
  providedIn: 'root'
})
export class WorkersService {

  constructor(private httpClient: HttpClient, private flightsService: FlightsService) {

    const nums$ = of(1, 2, 3, 4, 5, 6)
    const letters$ = of('10', '20', '30');


    interval(1000)
    .pipe(take(2))
    .pipe(
      switchMap(() => {
        return nums$
      })
    )
      .pipe(map(n => {
        if (n === 3) {
          throw ('E number')
        }
        return n;
      }), catchError((err) => { throw 'err' }))
      .subscribe(
        {
          next: (v) => console.log('v', v),
          error: (e) => {
            console.log('e', e);
            retry(3)
          }
        }
      );
    // concat(interval(1000), letters$, nums$)
    //   .pipe(retry(3))
